package week1.day2;

import java.util.Iterator;

public class FindOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=10;
		
		for (int i = 1; i <= num; i++) {
			
			if(i%2==1) {
				
			System.out.println("The Number is Odd "  +i);
			
		}
		
			
		}
		

	}

}
